import React from 'react';
import { useState } from 'react';
import { changeNewMessage } from '../../../Redux/State';
import otpravit from './message.png'
import S from './MessengerSupport.module.css'

let createAddMessageAction = (text) =>
{
return{
  type:'ADD_MESSAGE',
   message:text 
}
}
let createChangeNewMessageAction = (text) =>
{
return{
  type:'CHANGE_NEW_MESSAGE',
   textFromTextarea:text
}
}
const MessengerSupport = (props) => 
{
  let AllMessages = props.Messages.map((message) =>
   {return <div className = {S.messageArea}>{message.message}</div>})
  let textareaRef = React.createRef();
  let AddMessage = () => 
  {
    let text = textareaRef.current.value;
    props.dispatch(createAddMessageAction(text))
    textareaRef.current.value = ""
  }
  let ChangeNewMessage = () =>
  {
    let text = textareaRef.current.value;
    props.dispatch(createChangeNewMessageAction(text))
  }
 return(
   <div className ={S.messageBoard}>
     <div className = {S.allMessages}>
     {AllMessages}
     </div>
     <div className={S.textareaWithButton}>
     <textarea ref={textareaRef} value={props.NewMessage.text} onChange={ChangeNewMessage}></textarea>
     <button  onClick = {AddMessage}>
       click
     </button>
     </div>
   </div>
 )
}

export default MessengerSupport;
