import React from 'react';
import S from './Interes.module.css'
import { useState } from 'react';

const Text = (props) =>
{
const [val,SetVal] = useState("");
const HandleChange = (event) =>
{
SetVal(event.target.value)
}
if(props.show)
{
    props.updateData(val)
    return <textarea onChange={HandleChange}></textarea>
}
else 
{
    return <div></div>
}
}
const Butt = (props) =>
{
if(props.show)
{
    return  <button>
    Готово
    </button>
}
else 
{
    return <div></div>
}
}


const Interes = () => 
{
   
    const [showVvod, setShowVvod] = useState(false)
    const [inf, setInf] = useState("Напишите о своих интересах")
    
    return(
        <div>
            <div onClick={() =>setShowVvod(true)}>{inf}</div>
          <Text show={showVvod} updateData={setInf}/>
          <div onClick={() =>{setShowVvod(false); }}>
   <Butt show={showVvod} />
     </div>
     </div>
    )
}

export default Interes;
