import React from 'react';
import S from './Interes.module.css'
import { useState } from 'react';

const Text = (props) =>
{
if(props.show)
{
    
    return (
    <div>
        <textarea></textarea>
        <button onClick={() =>setShowVvod(false)} >
        Готово
        </button>
        </div>
    )
}
else 
{
    return <div></div>
}
}

const Interes = () => 
{
    const [showVvod, setShowVvod] = useState(false)
    const [interes, setInteres] = useState("Напишите о своих интересах")
    return(
        <div>
            <div onClick={() =>setShowVvod(true)}>{interes}</div>
          <Text show={showVvod} />
    
     
     </div>
    )
}

export default Interes;
