import {createAction, createReducer} from '@reduxjs/toolkit'

//let ADD_MESSAGE = 'ADD_MESSAGE';
//let CHANGE_NEW_MESSAGE = 'CHANGE_NEW_MESSAGE';



export const addMessage = createAction('ADD_MESSAGE')
export const changeNewMessage = createAction('CHANGE_NEW_MESSAGE', (text) => {return text})


  let initialState =
{
        Messages:
        [
          
        ],
        NewMessage:{text:""},
        messageCount:0
}
  

let MessengerSupportReducer = createReducer( initialState, (builder) =>
  {
    builder
    .addCase(addMessage, (state) =>
    {
      state.messageCount++;
      let newMessage =
      {
        id:state.messageCount, message:state.NewMessage
      }
      state.Messages.push(newMessage)
      
    })
    .addCase(changeNewMessage, (state, action)=>
    {
      state.NewMessage = action.payload.text
    }
    )
  }
)

export default MessengerSupportReducer;
