import Profile from './Profile/Profile'
import users from '../../../Source/Users';



class Profiles extends React.Component
{
  give3 =(massiv) =>
  {
    let massiv3 =[]
    for(let i=1; i<=3; i++)
    {
      
      massiv3.push(massiv.shift())
    }
    return massiv3;
  }
 

componentDidMount() 
{
  this.props.setUsers(
  this.give3(users)
  )
}
onButtonClick = () =>
{
  this.props.setUsers(
    this.give3(users)
    )
}
    render = () =>
    {
return(
  <div className={S.profiles}>
      <div >
        
        {this.props.Profiles.PrInf.map((mini) =>  (<div className={S.profile}>
    <Profile country={mini.country} city={mini.city} age={mini.age} photo={mini.photo} name={mini.name} id={mini.id} key = {mini.id}/>
    </div>) )}
    </div>
    <button className={S.but} onClick={this.onButtonClick}></button>
    </div>
    )
        }
}

export default Profiles;
