const WhoFind = () => 
{
   let textareaRef = React.createRef()
    const ShowAlert = () =>
    {
        let text = textareaRef.current.value;
        
        return(
            <div>
                {text}
            </div>
        )
    }
    
    return(
        <div>
    <textarea ref = {textareaRef} ></textarea>
    <button onClick={ShowAlert}>aa</button>
    <ShowAlert />
     </div>
    )
}

export default WhoFind;
