import React from 'react';
import S from './Inf.module.css'

const Inf = (props) => 
{
      const AreaForText = (props1) =>
{
      if (props.YourProfile.isChangableName && props1.pole==1)
      {
            
                              return (
                                  <div><input autoFocus={true} type="text" onChange={onNameInputChange} value={props.YourProfile.newData.newName}/></div>  
                              )
      }
      else if (props.YourProfile.isChangableAge && props1.pole==2)
      {
            return (
                  <div><input autoFocus={true} type="text" onChange={onAgeInputChange} value={props.YourProfile.newData.newName}/></div>  
              )
      }
             else
      {
            return(
                  <div>

                  </div>
            )
      }
}
let onNameInputChange = (e) =>
{
let text = e.target.value
props.changeNewName(text)
}
let onAgeInputChange = (e) =>
{
let text = e.target.value
props.changeNewAge(text)
}
let onNameButtonClick = ()=>
{
      props.letChangingName()
      props.changeName()
}

    return(
     
     <div className={S.inf}>
      <div className={S.Polya}>
       <div className={S.name}>
      {props.YourProfile.name}  <AreaForText pole={1} key='pole1'/> <button onClick={onNameButtonClick}></button>
      </div>
<div className={S.GenderAge}>
      <h1>Пол, возраст:</h1>
     
      {props.YourProfile.gender}, {props.YourProfile.age} <AreaForText pole={2} />
      </div>
      <div className={S.CountryCity}>
      <h1>Страна, город:</h1>
      {props.YourProfile.country}, {props.YourProfile.city}<AreaForText />
      </div>
      <div className={S.interes}>
            
<h1>Интересы</h1>
{props.YourProfile.interes}<AreaForText />
      </div>
      <div className={S.meet}>
<h1>Познакомлюсь</h1>
{props.YourProfile.meet}<AreaForText />
      </div>
      <div className={S.about}>
<h1>Расскажите о себе</h1>
{props.YourProfile.about}<AreaForText />
      </div>
      <div className={S.whoFind}>
<h1>Кого ищу</h1>
{props.YourProfile.whoFind}<AreaForText />
      </div>
      
      </div>
      
     </div>
    )
}

export default Inf;
