     message:text 
  })
  export let changeNewMessageActionCreator = (text) =>
  ({
  
    type:CHANGE_NEW_MESSAGE,
     textFromTextarea:text
  
  })

  
let messageCount = 0;
let MessageSupportReducer = (contentPageState, action) =>
{
switch(action.type)
{
  case ADD_MESSAGE :
  {
   
      messageCount = messageCount+1;
      let newMessage =
      {
        id:messageCount, message:action.message
      }
      contentPageState.Messages.push(newMessage)
  }
  case CHANGE_NEW_MESSAGE:
  {
    contentPageState.NewMessage.text = action.textFromTextarea;
  }
}
return contentPageState
}
export default MessageSupportReducer
