import React from 'react';
import S from './Interes.module.css'
import { useState } from 'react';
function Interes()
{
    const [interes, setInteres] = useState("Ебаться в жопу")
    const [showVvod, setShowVvod] = useState(true)
    return(
        <div className={S.wrapper}>
     <div className={S.Interes} onClick={() => setShowVvod(true)}>
         {showVvod === false && <div>{interes}</div>}
         {showVvod === true && <textarea
            value={interes}
            onChange={(e) => setInteres(e.target.value)}
         />}
         {showVvod === true && <button onClick={() => {
             setShowVvod(prevShowVvod => !prevShowVvod)
             console.log(showVvod)
        }} >Убрать интересы</button>}
     </div>
     </div>
    )
}

export default Interes;
