import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';
import { NavLink } from 'react-router-dom';
import S from './Login.module.css'

const Button = (props) =>
{
    let letEnter=()=>
    {
   
       props.changeName()
       props.letEnter()
    }
    let dontLetEnter = (e) =>
    {
      e.preventDefault()
     
    }
    return(
          (props.Login.areFilled.isNameFilled != "" && props.Login.areFilled.isEmailFilled)
        ?
        <NavLink to="/mimba/Swiper" >  <button onClick={letEnter}>Готово</button></NavLink>
        :<button onClick={dontLetEnter}>Готово</button> 
    )
}


const Login = (props) =>
{

     let [name, setName] = useState('')
    
let goSetName = (e)=>
{
  setName(e.target.value)
}

 useEffect( (name) =>
 {

props.checkIfNameFilled(name)
props.changeNewName(name)

 }, [name]
 )
 let getEmail = (e) =>
 {
let text = e.target.value
props.checkIfEmailFilled(text)

 }



 return(
        
<div className={S.Login}>
    РЕГИСТРАЦИЯ
  <form> 
<div> <input onChange={goSetName} type= "text" /> Ваше имя
      <input onChange={getEmail}  type= "email" /> Ваш Email
</div> 
<Button Login={props.Login} checkIfEmailFilled={props.checkIfEmailFilled}
 checkIfNameFilled={props.checkIfNameFilled} changeName={props.changeName} 
 letEnter={props.letEnter} />
</form> 

</div>
    )
}
export default Login
