 const AreaForText = (props) =>
{
      if (props.YourProfile.isChangableName && props.pole==1)
      {
            
                              return (
                                  <input  type="text" onChange={onNameInputChange} value={props.YourProfile.newData.newName}/>  
                              )
      }
      else if (props.YourProfile.isChangableAge && props.pole==2)
      {
            return (
                  <input autoFocus={true} type="text" onChange={onAgeInputChange} value={props.YourProfile.newData.newAge}/> 
              )
      }
             else
      {
            return(
                  <div>

                  </div>
            )
      }
}
