    const [interes, setInteres] = useState(<div onClick={() =>setShowVvod(<textarea></textarea>)}>Напишите о своих интересах</div>)
    return(
        <div>
     {interes}
     {showVvod}
     <button onClick = {() =>setShowVvod()}></button>
     </div>
    )
}

export default Interes;
