import React from 'react';
import S from './Profiles.module.css'
import Profile from './Profile/Profile'
import users from '../../../Source/Users';



class Profiles extends React.Component
{
  give6 =(massiv) =>
  {
    let massiv6 =[]
    for(let i=1; i<=6; i++)
    {
      
      massiv6.push(massiv.shift())
    }
    return massiv6;
  }
  returnFromState =(users) =>
  {
    
    let massivFromState = this.props.Profiles.PrInf
    return massivFromState.concat(users)
  }
 

componentDidMount() 
{
  this.props.setUsers(
  this.give6(users)
  )
}
componentWillUnmount()
{
  users.splice(0,users.length);
  users.push(...this.returnFromState(users))
 

}
onButtonClick = () =>
{
  this.props.setUsers(
    this.give6(users)
    )
}
    render = () =>
    {
return(
  <div className={S.profiles}>
      
        
        {this.props.Profiles.PrInf.map((mini) =>  (<div className={S.profile}>
    <Profile country={mini.country} city={mini.city} age={mini.age} photo={mini.photo} name={mini.name} id={mini.id} key = {mini.id}/>
    </div>) )}
    
    <button className={S.button} onClick={this.onButtonClick}>загрузить ещё</button>
   </div>
    )
        }
}

export default Profiles;
