import React from 'react';
import S from './Interes.module.css'
import { useState } from 'react';

const Text = (props) =>
{
    [val, setVal] = useState("")
    const HandleChange = (event) =>
    {
        val = event.target.value
    }
    props.updateData = val;
if(props.show)
{
    return <textarea value = {val} onChange = {HandleChange}></textarea>
}
else 
{
    return <div></div>
}
}
const Butt = (props) =>
{
if(props.show)
{
    return  <button>
    Готово
    </button>
}
else 
{
    return <div></div>
}
}


const Interes = () => 
{
   
    const [showVvod, setShowVvod] = useState(false)
    const [interes, setInteres] = useState("Напишите о своих интересах")
    function updateData(value) {
        interes = value;
    }
    return(
        <div>
            <div onClick={() =>setShowVvod(true)}>{interes}</div>
          <Text show={showVvod} updateData={updateData} />
          <div onClick={() =>{setShowVvod(false); }}>
   <Butt show={showVvod} />
     </div>
     </div>
    )
}

export default Interes;
