let CHANGE_NAME = "CHANGE_NAME"
let CHANGE_AGE = "CHANGE_AGE"
let CHANGE_COUNTRY = "CHANGE_COUNTRY"
let CHANGE_CITY = "CHANGE_CITY"
let CHANGE_GENDER = "CHANGE_GENDER"
let CHANGE_ABOUT = "CHANGE_ABOUT"
let CHANGE_INTERES = "CHANGE_INTERES"
let CHANGE_MEET = "CHANGE_MEET"
let CHANGE_WHOFIND = "CHANGE_WHOFIND"

let ChangeNameActionCreator = (text) =>
{
    return {
        type : CHANGE_NAME,
        text : text
    }
}

let ChangeAgeActionCreator = (text) =>
{
    return {
        type : CHANGE_AGE,
        text : text
    }
}

let ChangeCountryActionCreator = (text) =>
{
    return {
        type : CHANGE_COUNTRY,
        text : text
    }
}

let ChangeCityActionCreator = (text) =>
{
    return {
        type : CHANGE_CITY,
        text : text
    }
}

let ChangeGenderActionCreator = (text) =>
{
    return {
        type : CHANGE_GENDER,
        text : text
    }
}

let ChangeAboutActionCreator = (text) =>
{
    return {
        type : CHANGE_ABOUT,
        text : text
    }
}

let ChangeInteresActionCreator = (text) =>
{
    return {
        type : CHANGE_INTERES,
        text : text
    }
}

let ChangeMeetActionCreator = (text) =>
{
    return {
        type : CHANGE_MEET,
        text : text
    }
}

let ChangeWhoFindActionCreator = (text) =>
{
    return {
        type : CHANGE_WHOFIND,
        text : text
    }
}
let initialState =
{
name:'введите ваше имя',
age: 'введите ваш возраст',
country: 'Ваша страна',
city:'город',
gender:'пол',
about: 'Добавьте информацию о себе',
interes:'Добавьте информацию о себе',
meet:'Добавьте информацию о себе',
whoFind:'Добавьте информацию о себе'
}

let YourProfileReducer = (state = initialState, action) =>
{
    switch(action.type)
    {
        case CHANGE_NAME:{
            return {
                ...state,
                name:action.text
            }
        }
        case CHANGE_AGE:{
            return {
                ...state,
                age:action.text
            }
        }
        case CHANGE_COUNTRY:{
            return {
                ...state,
                country:action.text
            }
        }
        case CHANGE_CITY:{
            return {
                ...state,
                city:action.text
            }
        }
        case CHANGE_GENDER:{
            return {
                ...state,
                gender:action.text
            }
            
        }
        case CHANGE_ABOUT:{
            return {
                ...state,
                about:action.text
            }
    }
    case CHANGE_INTERES:{
        return {
            ...state,
            interes:action.text
        }
}
case CHANGE_MEET:{
    return {
        ...state,
        meet:action.text
    }
}
case CHANGE_WHOFIND:{
    return {
        ...state,
        whoFind:action.text
    }
}
    }
}
export default YourProfileReducer
