import React from 'react'
import MessengerSupport from './MessengerSupport'
import {addMessageActionCreator, changeNewMessageActionCreator} from '../../../Redux/MessengerSupportReducer'
import { connect } from 'react-redux';
const MessengerSupportContainer = (props) => 
{
let state = props.store.getState()
  let AddMessage = (text) => 
  {
    props.store.dispatch(addMessageActionCreator(text))
    props.store.dispatch(changeNewMessageActionCreator(""))
  }
  let ChangeNewMessage = (text) =>
  {
    props.store.dispatch(changeNewMessageActionCreator(text))
  }
 return(
   <MessengerSupport MessengerSupport={state.MessengerSupport}
    AddMessage={AddMessage} ChangeNewMessage={ChangeNewMessage} />
 )
}

export default MessengerSupportContainer;
